import React, { useContext, useEffect, useState } from "react";
import {
  Stack,
  Center,
  Heading,
  VStack,
  Divider,
  Pressable,
  NativeBaseProvider,
  Text,
  Container,
  Box,
  HStack,
  FlatList,
  Image,
  ScrollView
} from "native-base";
import { useIsFocused, useNavigation } from '@react-navigation/native';
import { ImageBackground, StyleSheet, TouchableOpacity } from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { PointContext,LoadingContext } from "../context/context";

export const Home = ({ navigation }) => {

  const Navigation = useNavigation(); 
  const isFocused = useIsFocused();

  const [userData, setUserData] = useState(null);
  const { points } = useContext(PointContext);
  const { setIsLoading } = useContext(LoadingContext);

  //get user data
  const getUserData = async () => {
    const ac = new AbortController();
    setIsLoading(true);
    try {
      const value = await AsyncStorage.getItem('user');
      if (value !== null) {
        // We have data!!
        setUserData(JSON.parse(value));
      }
      setIsLoading(false);
    } catch (error) {
      // Error retrieving data
      console.log(error);
    }
    return () => ac.abort();
  }; 

  //get userData
  useEffect(() => {
    getUserData();
  }, [isFocused])

  return (
    <ImageBackground source={require('../public/assets/bg-1-100.jpg')} style={styles.image}>

    <HStack style={styles.box} justifyContent="space-between" alignItems="center" bg="#1FAA8F" p="4">
      <Box>
        <Heading size="sm" color="white">Welcome, {userData && userData.name}!</Heading>
        <Text color="white" mt="1" fontSize="16">{points} pts</Text>
      </Box>
      <TouchableOpacity onPress={()=>Navigation.navigate('Reward')}>
        <Box backgroundColor="#FC8B10" px="3" py="1" rounded="xl" mt="5">
          <Text fontSize="sm" color="white">Redeem rewards</Text>
        </Box>
      </TouchableOpacity> 
    </HStack>

      <ScrollView p="3">
        <Heading size="md" mt="2" bold>Categories</Heading>
        <Stack direction="row" mb="2" mt="2" space={3}>
          <Pressable onPress={()=>Navigation.navigate('GeneralWaste')}>
            <VStack alignItems="center" space={1}>
            <Center
            size="20"
            bg="#F4F9F8"
            rounded="lg"
            shadow={"3"}>
              <Box size="20" overflow="hidden">
                <Image style={styles.icon} maxHeight="14%" maxWidth="14%" left="30%" top="10%" source={require('../public/assets/recycle.png')} alt="Alternate Text"/>
              </Box>
            </Center>
            <Text fontSize={12}>General Waste</Text>
            </VStack>
          </Pressable>

          <Pressable onPress={()=>Navigation.navigate('EWaste')}>
          <VStack alignItems="center" space={1}>
            <Center
            size="20"
            bg="#F4F9F8"
            rounded="lg"
            shadow={"3"}>
              <Box size="20" overflow="hidden">
                <Image style={styles.icon} maxHeight="16%" maxWidth="16%" left="30%" top="1%" source={require('../public/assets/ewaste.png')} alt="Alternate Text"/>
              </Box>
            </Center>
            <Text fontSize={12}>E-Waste</Text>
            </VStack>
          </Pressable>

          <Pressable onPress={()=>Navigation.navigate('FoodWaste')}>
          <VStack alignItems="center" space={1}>
            <Center
            size="20"
            bg="#F4F9F8"
            rounded="lg"
            shadow={"3"}>
              <Box size="20" overflow="hidden">
                <Image style={styles.icon} maxHeight="13%" maxWidth="13%" left="30%" top="12%" source={require('../public/assets/healthy-food.png')} alt="Alternate Text"/>
              </Box>
            </Center>
            <Text fontSize={12}>Food Waste</Text>
            </VStack>
          </Pressable>

          <Pressable onPress={()=>Navigation.navigate('CookingOil')}>
          <VStack alignItems="center" space={1}>
            <Center
            size="20"
            bg="#F4F9F8"
            rounded="lg"
            shadow={"3"}>
              <Box size="20" overflow="hidden">
                <Image style={styles.icon} maxHeight="13%" maxWidth="13%" left="30%" top="10%" source={require('../public/assets/oil.png')} alt="Alternate Text"/>
              </Box>
            </Center>
            <Text fontSize={12}>Cooking Oil</Text>
            </VStack>
          </Pressable>
        </Stack>

      <Box shadow="4" mt="3">
          <Box p="3" w="full" style={styles.container} overflow="hidden">
            <Image source={require('../public/assets/game_bg.png')} style={{position:"absolute", right:-20, top:-80}} alt="Alternate Text" size="56"/>
              <HStack justifyContent="space-between" px="3" py="2">
                <VStack justifyContent="space-between" w="200">
                  <Heading mb="2" fontSize="lg">Play To Learn & Earn</Heading>
                  <Text color="gray.600">Play mini games and earn MR Points while learning how to recycle.</Text>
                </VStack>
                <VStack justifyContent="flex-end" space={1}>
                  <TouchableOpacity onPress={()=>Navigation.navigate("Game")}>
                    <Center backgroundColor="#FC8B10" px="3" py="1" rounded="2xl" borderWidth="1" borderColor="#FC8B10">
                      <Text fontSize="sm" color="white" bold>Play Now</Text>
                    </Center>
                  </TouchableOpacity> 
                </VStack>
              </HStack>
          </Box>
      </Box>


        {/* <Heading size="md" mt="5" bold>Rewardable</Heading> */}
        {/* <FlatList
            data={data}
            renderItem={({ item }) => (
              <Box
                borderBottomWidth="1"
                _dark={{
                  borderColor: "gray.600",
                }}
                borderColor="coolGray.200"
                px="1"
                py="2"
                backgroundColor="#EFF9F6"
              >
               <Pressable onPress={(e)=>openModal(e, item)}>

                <HStack space={3} justifyContent="space-between">
                  <VStack>
                    <Text
                      _dark={{
                        color: "warmGray.50",
                      }}
                      color="coolGray.800"
                      bold
                    >
                      Location A
                    </Text>
                    <Text
                      color="coolGray.600"
                      _dark={{
                        color: "warmGray.200",
                      }}
                    >
                      Address A
                    </Text>
                  </VStack>
                </HStack>
                </Pressable>
              </Box>

            )}
            keyExtractor={(item) => parseInt(item)}
          /> */}


      </ScrollView>
      </ImageBackground>
  );
};

export default () => {
  return (
    <NativeBaseProvider>
      <Center flex={1}>
        <Home />
      </Center>
    </NativeBaseProvider>
  )
};

const styles = StyleSheet.create({
  image: {
      flex: 1,
      width: '100%',
      alignItems: 'center',
  },
  box:{
    width:'100%',
  },
  icon:{
    position: "absolute",
  },
  container:{
    shadowColor: '#171717',
    shadowOffset: {width: 0, height: 0},
    shadowOpacity: 0.2,
    shadowRadius: 8,
    elevation: 20,
    backgroundColor: 'white',
    borderRadius:14
  }
});
