import React from "react";

export const AuthContext = React.createContext();

export const PointContext = React.createContext({
    points: 0,
    setPoints: (points) => {} 
});

export const LoadingContext = React.createContext({
    isLoading: false,
    setIsLoading: (isLoading) => {} 
});
